import './App.css';
import React, {useRef} from 'react';

import "./CSS/index";
import { Navbar } from "./Layout/index";
import {ForwaredHello,ForwaredResume, ForwaredPortfolio, ForwaredContact} from "./Pages/index"

const App = () => {

  const refHello = useRef(null);
  const refResume = useRef(null);
  const refPortfolio = useRef(null);
  const refContact = useRef(null);

  return (
    <div className="App">
      <Navbar/>
      <button onClick={() => {
        refResume.current.scrollIntoView({
          behavior: "smooth",
          block: "start"
        });
      }}>AQUI</button>
      <div>
      <ForwaredHello ref={refHello} />
      <ForwaredResume ref={refResume} />
      <ForwaredPortfolio ref={refPortfolio} />
      <ForwaredContact ref={refContact} />
      </div>
    </div>
  );
}

export default App;
